java -jar SAPP/InterProScan.jar -i data/rdf/sequences.hdt -o data/rdf/sequences_ipr.hdt -a Pfam
java -jar SAPP/HDTQuery.jar -i data/rdf/sequences_ipr.hdt -query "PREFIX gbol:<http://gbol.life/0.1/> SELECT ?geneId ?function ?begin ?eind ?xref WHERE {{ ?protein a gbol:Protein. ?protein gbol:note ?note. ?note a gbol:Note. ?note gbol:text ?geneId. ?protein gbol:feature ?feature. ?feature a gbol:ProteinDomain. ?feature gbol:signatureDesc ?function. ?feature gbol:location ?loc. ?loc a gbol:Region. ?loc gbol:begin ?beginPos. ?loc gbol:end ?endPos. ?beginPos gbol:position ?begin. ?endPos gbol:position ?eind. ?feature gbol:xref ?xref }}" | tee data/interProData.txt
